mapper.update:
UPDATE board_review
SET reviewTitle=#{reviewTitle}, content=#{content}, rating=#{rating}
WHERE num=#{num}

UPDATE board_review
SET viewCount=viewCount+1
WHERE num=#{num}

mapper.select:
SELECT num, isbn, imagePath, writer,reviewTitle,content,viewCount,regdate,spoCheck,rating
FROM board_review
WHERE num=#{num}
